mapper.select:
SELECT a.*, u.id AS "createBy.id", u.name AS "createBy.name", c.name AS "createBy.company.name", o.name AS "createBy.office.name" FROM sys_log a left JOIN sys_user u ON u.id = a.create_by left JOIN sys_office c ON c.id = u.company_id left JOIN sys_office o ON o.id = u.office_id WHERE a.create_date BETWEEN #{beginDate} AND #{endDate}
AND a.title LIKE '%'||#{title}||'%' '%'||#{title}||'%' CONCAT('%', #{title}, '%') '%'+#{title}+'%'
AND u.name = #{createBy.name}
AND a.request_uri LIKE '%'||#{requestUri}||'%' '%'||#{requestUri}||'%' CONCAT('%', #{requestUri}, '%') '%'+#{requestUri}+'%'
AND a.type = #{type}
ORDER BY ${page.orderBy}
ORDER BY a.create_date DESC

mapper.delete:
delete from sys_log
where id IN

	#{id}
