mapper.select:
SELECT

FROM plugin_datasource_link a

WHERE a.id = #{id}

SELECT

FROM plugin_datasource_link a



    ${dataScope}

        AND a.name LIKE
        '%'||#{name}||'%'
        '%'||#{name}||'%'
        '%'+#{name}+'%'
        concat('%',#{name},'%')


        AND a.type = #{type}


        AND a.dbname = #{dbname}

       AND a.id != 'master'



        ORDER BY ${page.orderBy}


        ORDER BY a.update_date DESC

SELECT

FROM plugin_datasource_link a



    ${dataScope}
    AND a.id != 'master'



        ORDER BY ${page.orderBy}


        ORDER BY a.update_date DESC

mapper.update:
UPDATE plugin_datasource_link SET
del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
