mapper.select:
SELECT

FROM plugin_datasource_model_meta a

WHERE a.id = #{id}

SELECT
FROM plugin_datasource_model_meta a
${dataScope}
AND a.dsId = #{dataSet.id}
AND a.isneed = #{isNeed}
AND a.name LIKE '%'||#{name}||'%' '%'||#{name}||'%' '%'+#{name}+'%' concat('%',#{name},'%')
ORDER BY a.sort asc

mapper.update:
UPDATE plugin_datasource_model_meta SET
del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
