mapper.select:
SELECT

FROM plugin_datasource_model_params a

WHERE a.id = #{id}

SELECT

FROM plugin_datasource_model_params a



    ${dataScope}

        AND a.dsId = #{dataSet.id}


        AND a.field = #{field}


        AND a.defaultValue = #{defaultValue}


ORDER BY a.sort asc

mapper.update:
UPDATE plugin_datasource_model_params SET
dsId = #{dataSet.id},
field = #{field},
defaultValue = #{defaultValue}
WHERE id = #{id}

UPDATE plugin_datasource_model_params SET
del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
