FROM plugin_datasource_model a
${dataScope}
AND a.db = #{db.id}
AND a.name LIKE '%'||#{name}||'%' '%'||#{name}||'%' '%'+#{name}+'%' concat('%',#{name},'%')
ORDER BY ${page.orderBy}
ORDER BY a.update_date DESC

