mapper.select:
SELECT

FROM act_extension_category a

WHERE a.id = #{id}

SELECT a.id, a.parent_id AS "parent.id", a.parent_ids FROM act_extension_category a
AND a.parent_ids LIKE #{parentIds}
ORDER BY a.sort ASC

mapper.update:
UPDATE act_extension_category SET
	parent_id = #{parent.id},
	parent_ids = #{parentIds}
WHERE id = #{id}
