mapper.select:
SELECT

FROM act_extension_cc a

WHERE a.id = #{id}

SELECT
FROM act_extension_cc a
a.userid = #{currentUser.id}
AND a.procinsname LIKE '%'||#{procInsName}||'%' '%'||#{procInsName}||'%' '%'+#{procInsName}+'%' concat('%',#{procInsName},'%')
ORDER BY ${page.orderBy}

SELECT
FROM act_extension_cc a
${dataScope}
AND a.procinsname LIKE '%'||#{procInsName}||'%' '%'||#{procInsName}||'%' '%'+#{procInsName}+'%' concat('%',#{procInsName},'%')
ORDER BY ${page.orderBy}

mapper.update:
UPDATE act_extension_cc SET
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
