mapper.select:
SELECT

FROM act_extension_form_def a

WHERE a.id = #{id}

SELECT , c.id AS "formDefinitionJson.id", c.json AS "formDefinitionJson.json", c.version AS "formDefinitionJson.version", c.status AS "formDefinitionJson.status", c.is_primary AS "formDefinitionJson.isPrimary" FROM act_extension_form_def a
LEFT JOIN act_extension_form_def_json c ON c.form_definition_id = a.id
a.del_flag = #{DEL_FLAG_NORMAL} ${dataScope}
AND (b.id = #{category.id} OR b.parent_ids LIKE '%,'||#{category.id}||',%') '%,'||#{category.id}||',%') CONCAT('%,', #{category.id}, ',%')) '%'+#{category.id}+'%')
AND ( c.is_primary = '1' or c.is_primary is null)
AND a.name = #{name}
AND c.status = #{formDefinitionJson.status}
ORDER BY ${page.orderBy}
ORDER BY a.create_date ASC

SELECT

FROM act_extension_form_def a


	a.del_flag = #{DEL_FLAG_NORMAL}
	${dataScope}



		ORDER BY ${page.orderBy}


		ORDER BY a.create_date ASC

mapper.update:
DELETE FROM act_extension_form_def


		WHERE id = #{id}


		WHERE category_id = #{category.id}

UPDATE act_extension_form_def SET
	del_flag = #{DEL_FLAG_DELETE}


		WHERE id = #{id}


		WHERE category_id = #{category.id}
